INSERT INTO `exm_question_lib` VALUES (1963, 1, '“四个全面”战略布局是一个整体，其中，____是战略目标。', 'A、基本实现现代化，达到中等发达国家水平', 'B、全面依法治国', 'C、全面从严治党', 'D、全面建成小康社会', 'D', '暂无', 1);
INSERT INTO `exm_question_lib` VALUES (1964, 1, '毛泽东指出，马克思主义一定要“和民族的特点结合，经过一定的民族形式”表现出来。表明____。', 'A、马克思主义一定要和中国传统文化结合，才能求得发展', 'B、马克思主义指导中国革命、建设和改革的实践', 'C、马克思主义指导民族革命和建设事业', 'D、将马克思主义与中国优秀文化相结合，使马克思主义根植于中国优秀文化的土壤之中得以生长和繁荣', 'D', '暂无', 1);
INSERT INTO `exm_question_lib` VALUES (1965, 1, '中国共产党找到了马克思主义这个崭新的思想武器，并不意味着就能够自然而然地解决中国革命所面临的问题。还有一个____。', 'A、马克思主义理论适合西方国家，未必适合东方国家', 'B、马克思主义理论需要在实践中修正', 'C、马克思主义产生于发达资本主义国家，不适合小农经济国家', 'D、如何实现马克思主义中国化的问题', 'D', '暂无', 1);
INSERT INTO `exm_question_lib` VALUES (1966, 1, '马克思主义中国化的两个目标是______。　　', 'A、将马克思主义基本原理同中国具体实际和时代特征结合起来，坚持和发展马克思主义；不断形成具有中国特色的马克思主义理论成果的过程　　', 'B、“解决中国问题”和“创造些新的东西”　　', 'C、反对教条主义和经验主义，坚持中国共产的领导　　', 'D、坚持调查研究和走群众路线，在实践中理论创新', 'A', '暂无', 1);
INSERT INTO `exm_question_lib` VALUES (1967, 1, '毛泽东在1938年党的六届六中全会上作了《论新阶段》的政治报告中，党首次明确提出了____重大命题,对后来党的理论发展和事业产生了深远的影响。', 'A、学习马克思主义', 'B、学习十月革命经验', 'C、马克思主义中国化', 'D、建设社会主义', 'C', '暂无', 1);
INSERT INTO `exm_question_lib` VALUES (1968, 1, '中国特色社会主义理论体系包括____。', 'A、毛泽东思想、邓小平理论和“三个代表”重要思想', 'B、毛泽东思想、邓小平理论、“三个代表”重要思想和科学发展观', 'C、邓小平理论、“三个代表”重要思想和科学发展观', 'D、邓小平理论和“三个代表”重要思想', 'C', '暂无', 1);
INSERT INTO `exm_question_lib` VALUES (1969, 1, '党的十八大以来，______是中国特色社会主义理论体系的最新成果。', 'A、毛泽东思想', 'B、邓小平理论', 'C、科学发展观', 'D、习近平总书记发表一系列重要讲话', 'D', '暂无', 1);
INSERT INTO `exm_question_lib` VALUES (1970, 1, '毛泽东思想和中国特色社会主义理论体系共同理论基础是____。', 'A、中国革命和建设所面对的现实状况', 'B、世界格局的演变，不断出现新情况和新问题', 'C、马克思列宁主义', 'D、苏联革命和社会主义建设经验', 'C', '暂无', 1);
INSERT INTO `exm_question_lib` VALUES (1971, 1, '1997年召开的党的十五大正式提出____概念，并作为党的指导思想写入党章。', 'A、科学发展观', 'B、群众路线', 'C、三个代表重要思想', 'D、邓小平理论', 'D', '暂无', 1);
