INSERT INTO `exm_question_lib` VALUES (1976, 1, '当代中国的最大国情是____。　　', 'A、建设中国特色社会主义，全面开创社会主义发展的新时代　　', 'B、推进马克思主义中国化，在实践中发展马克思主义　　', 'C、全面改革开放，不断加强综合国力，实现中华民族伟大复兴　　', 'D、处于并将长期处于社会主义初级阶段　', 'D', '暂无', 1);
INSERT INTO `exm_question_lib` VALUES (1977, 1, '毛泽东思想达到成熟的主要标志是___。', 'A、中华人民共和国成立，民主革命任务最终完成', 'B、创立了新民主主义革命理论', 'C、实现了向社会主义的转变，中国社会进入新的发展时期', 'D、社会主义改造完成', 'B', '暂无', 1);
