INSERT INTO `exm_question_lib` VALUES (1981, 1, '毛泽东思想的精髓是____。', 'A、揭示了中国革命的发展规律，建立新中国，开辟中国发展的新纪元', 'B、领导土地革命，调动了广大人民参加革命的积极性，奠定革命胜利的基础', 'C、制定科学的社会发展战略', 'D、实事求是', 'D', '暂无', 1);
INSERT INTO `exm_question_lib` VALUES (1982, 1, '毛泽东在论述政策和策略极端重要性时指出______。', 'A、政策极其重要，注意政策的稳定性', 'B、策略极其重要，注意策略的稳定性', 'C、必须根据政治形势、阶级关系和实际情况及其变化制定党的政策，原则性和灵活性结合。', 'D、政策是多变的', 'C', '暂无', 1);
INSERT INTO `exm_question_lib` VALUES (1983, 1, '“三个代表”重要思想用一系列紧密联系、相互贯通的新思想、新观点新论断创进一步回答了什么是社会主义，怎样建设社会主义的问题，创造性地回答了____的问题。', 'A、什么是马克思主义，怎样坚持和发展马克思主义', 'B、什么是社会主义，怎样建设社会主义', 'C、什么是小康社会，怎样建设小康社会', 'D、建设什么样的党，怎样建设党', 'D', '暂无', 1);
