INSERT INTO `exm_question_lib` VALUES (1992, 1, '中国共产党的思想路线简称为____。', 'A、马克思主义中国化', 'B、立党为公、执政为民', 'C、实事求是的思想路线', 'D、发展为了人民、发展依靠人民、发展成果由人民享有', 'C', '暂无', 1);
