INSERT INTO `exm_question_lib` VALUES (1995, 1, '建设中国特色社会主义理论体系是____。　', 'A、解放思想，实事求是的基础　　', 'B、社会主义初级阶段的理论　　', 'C、是姓“资”还是姓“社”的判断标准　　', 'D、新时期全党全国各民族人们团结奋斗的共同的思想基础', 'D', '暂无', 1);
