INSERT INTO `exm_question_lib` VALUES (1997, 1, '就毛泽东一生来看____。', 'A、功绩第一位的，错误第二位的', 'B、错误第一位的，功绩第二位的', 'C、功过相抵', 'D、主要功绩在社会主义建设时期', 'A', '暂无', 1);
INSERT INTO `exm_question_lib` VALUES (1998, 1, '对党的思想路线的内容首次作出概括的是______。', 'A、毛泽东', 'B、江泽民', 'C、刘少奇', 'D、邓小平', 'D', '暂无', 1);
